import "./WelcomePage.css"
import {useEffect, useState} from "react";

const WelcomePage = () => {
    const [gameData, setGameData] = useState(null);
    const [rounds, setRounds] = useState(0);
    const [company,setCompany] = useState("");

    const startGame = async () => {
        try {
            const response = await fetch('http://localhost:8080/game/start', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({rounds: rounds, company: company})
            });
            if (!response.ok) {
                throw new Error(`HTTP error! Status: ${response.status}`);

            }
            const data = await response.json();
            setGameData(data);
        } catch (error) {
            console.error("There was a problem with the fetch operation:", error.message);
        }

    }






    return (
        <div>
            <h2 className="text">Welcome to the RM-Game</h2>



            {gameData ? (  <div>
                <h3>Review your choices:</h3>
                <p>Chosen Rounds: {rounds}</p>
                <p>Chosen Company: {company}</p>
                <button onClick={startGame}>Confirm</button>
                <button onClick={() => setGameData(null)}>Change</button>
            </div>):
                (

            <div>
            <div>
                <p className="text">Please select how many Rounds you want to play</p>
                <select
                    className="form-select"
                    aria-label="Default select example"
                    onChange={e => setRounds(parseInt(e.target.value))}>
                    <option value="" selected>Select rounds</option>
                    <option value="1">One</option>
                    <option value="2">Two</option>
                    <option value="3">Three</option>
                    <option value="4">Four</option>
                </select>
            </div>
            <p className="text">Please select a Company</p>
            <div>
                <select
                    className="form-select"
                    aria-label="Default select example"
                    onChange={e => setCompany(e.target.value)}>
                    <option value="" selected>Select a Company</option>
                    <option value="Computer">Computer</option>
                    <option value="Stock">Stock</option>
                    <option value="Printing Plant">Printing Plant</option>
                    <option value="Tools">Tools</option>
                    <option value="Metal">Metal</option>
                </select>

            </div>

            <button type="submit" onClick={startGame}>Submit</button>

            </div>
                )}
        </div>
    )
}
export default WelcomePage;
