import "./WelcomePage.css"
import {useState} from "react";

const WelcomePage = () => {
    const [gameData, setGameData] = useState(null);
    const [rounds, setRounds] = useState(0);

    const startGame = async () => {
        try {
            const response = await fetch('/game/start', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({rounds: rounds})
            });
            if (!response.ok) {
                throw new Error(`HTTP error! Status: ${response.status}`);

            }
            const data = await response.json();
            setGameData(data);
        } catch (error) {
            console.error("There was a problem with the fetch operation:", error.message);
        }

    }
    startGame();


    return (
        <div>
            <h2 className="text">Welcome to the RM-Game</h2>
            <p className="text">Please select how many Rounds you want to play</p>

            <div>

                <select className="form-select" aria-label="Default select example">
                    <option value="" selected>Open this select menu</option>
                    <option
                        value="1"
                        onChange={e => setRounds(parseInt(e.target.value))}
                    >One
                    </option>
                    <option value="2"
                            onChange={e => setRounds(parseInt(e.target.value))}
                    >Two
                    </option>
                    <option
                        value="3"
                        onChange={e => setRounds(parseInt(e.target.value))}
                    >Three
                    </option>
                    <option
                        value="4"
                        onChange={e => setRounds(parseInt(e.target.value))}
                    >Four
                    </option>
                </select>
            </div>

            <button type="submit" onClick={() => console.log(rounds)}>Submit</button>

        </div>
    )
}
export default WelcomePage;
