import "./WelcomePage.css"
import {useEffect, useState} from "react";

const WelcomePage = () => {
    const [gameData, setGameData] = useState(null);
    const [rounds, setRounds] = useState(0);
    const [playerName,setPlayerName] = useState("");

    const startGame = async () => {
        try {
            const response = await fetch('http://localhost:8080/game/start', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({rounds: rounds, playername: playerName})
            });
            if (!response.ok) {
                throw new Error(`HTTP error! Status: ${response.status}`);

            }
            const data = await response.json();
            setGameData(data);
        } catch (error) {
            console.error("There was a problem with the fetch operation:", error.message);
        }

    }






    return (
        <div>
            <h2 className="text">Welcome to the RM-Game</h2>



            {gameData ? (  <div>
                <h3 className="text">Review your choices:</h3>
                <p className="text">Chosen Rounds: {rounds}</p>
                <p className="text">Chosen Name: {playerName}</p>
                <button onClick={startGame}>Confirm</button>
                <button onClick={() => setGameData(null)}>Change</button>
            </div>):
                (

            <div>
            <div>
                <p className="text">Please select how many Rounds you want to play</p>
                <select
                    className="form-select"
                    aria-label="Default select example"
                    onChange={e => setRounds(parseInt(e.target.value))}>
                    <option value="" selected>Select rounds</option>
                    <option value="1">One</option>
                    <option value="2">Two</option>
                    <option value="3">Three</option>
                    <option value="4">Four</option>
                </select>
            </div>
            <p className="text">Please Enter a Name</p>
            <div>
            <div class="input-group mb-3">
  <div class="input-group-prepend">
    <span class="input-group-text" id="basic-addon1">Enter Name</span>
  </div>
  <input type="text" class="form-control" placeholder="Username" aria-label="Username" aria-describedby="basic-addon1" onChange={e => setPlayerName(e.target.value)}/>
</div>

            </div>

            <button type="submit" onClick={startGame}>Submit</button>

            </div>
                )}
        </div>
    )
}
export default WelcomePage;
